import mongoose from 'mongoose';

const connectDB = async () => {
  try {
    const conn = await mongoose.connect(process.env.MONGODB_URI);
    console.log(`MongoDB Connected: ${conn.connection.host}`);
  } catch (error) {
    console.error('Database connection error:', error);
    process.exit(1);
  }
};

// User Schema
const userSchema = new mongoose.Schema({
  sponsorId: {
    type: String,
    required: true
  },
  ownSponsorId: {
    type: String,
    unique: true,
    required: true
  },
  firstName: {
    type: String,
    required: true
  },
  lastName: {
    type: String,
    required: true
  },
  mobile: {
    type: String,
    required: true,
    validate: {
      validator: function(v) {
        return /^\d{10}$/.test(v);
      },
      message: 'Mobile number must be exactly 10 digits'
    }
  },
  email: {
    type: String,
    required: true,
    unique: true,
    lowercase: true
  },
  password: {
    type: String,
    required: true
  },
  role: {
    type: String,
    enum: ['user', 'admin'],
    default: 'user'
  },
  isActive: {
    type: Boolean,
    default: true
  },
  referralCount: {
    type: Number,
    default: 0
  },
  parent: {
    type: mongoose.Schema.Types.ObjectId,
    ref: 'User',
    default: null
  },
  children: [{
    type: mongoose.Schema.Types.ObjectId,
    ref: 'User'
  }],
  level1Count: { type: Number, default: 0 },
  level2Count: { type: Number, default: 0 },
  level3Count: { type: Number, default: 0 },
  level4Count: { type: Number, default: 0 },
  level5Count: { type: Number, default: 0 },
  totalEarnings: { type: Number, default: 0 },
  level1Earnings: { type: Number, default: 0 },
  level2Earnings: { type: Number, default: 0 },
  level3Earnings: { type: Number, default: 0 },
  level4Earnings: { type: Number, default: 0 },
  level5Earnings: { type: Number, default: 0 },

  // Password Reset
  resetToken: { type: String, default: null },
  resetTokenExpiry: { type: Date, default: null },

  // DAS program fields
  dasEnrollmentDate: { type: Date, default: null },
  dasCountdownStartDate: { type: Date, default: null },
  isEnrolledInDas: { type: Boolean, default: false },
  totalInvestmentVolume: { type: Number, default: 0 },
  dasTask1Completed: { type: Boolean, default: false },
  dasTask2Completed: { type: Boolean, default: false },
  dasTask3Completed: { type: Boolean, default: false },
  dasTask1CompletedAt: { type: Date, default: null },
  dasTask2CompletedAt: { type: Date, default: null },
  dasTask3CompletedAt: { type: Date, default: null },
  dasMonthlyEarnings: { type: Number, default: 0 },

  // Investment tracking fields
  totalInvestmentAmount: { type: Number, default: 0 },
  directIncome: { type: Number, default: 0 }, // 6% from direct referrals
  fsIncome: { type: Number, default: 0 }, // FS (FixSix) monthly returns
  smartLineIncome: { type: Number, default: 0 }, // 5-tier commissions (already handled by levelXEarnings)
  walletBalance: { type: Number, default: 0 }, // Total withdrawable amount
  totalWithdrawn: { type: Number, default: 0 },
  dailyFsIncome: { type: Number, default: 0 }, // Today's FS income credit

  // KYC fields
  kycStatus: { 
    type: String, 
    enum: ['pending', 'approved', 'rejected'], 
    default: null 
  },
  kycDocumentUrl: { type: String, default: null },
  kycFileName: { type: String, default: null },
  kycFileType: { type: String, default: null },
  kycSubmittedAt: { type: Date, default: null },
  kycApprovedAt: { type: Date, default: null },
  kycRejectedAt: { type: Date, default: null },
  kycApprovedBy: { type: mongoose.Schema.Types.ObjectId, ref: 'User', default: null },
  kycRejectedBy: { type: mongoose.Schema.Types.ObjectId, ref: 'User', default: null },
  kycRejectionReason: { type: String, default: null }
}, {
  timestamps: true
});

// Contact Schema
const contactSchema = new mongoose.Schema({
  firstName: {
    type: String,
    required: true
  },
  lastName: {
    type: String,
    required: true
  },
  email: {
    type: String,
    required: true
  },
  subject: {
    type: String,
    required: true
  },
  message: {
    type: String,
    required: true
  }
}, {
  timestamps: true
});

// Newsletter Schema
const newsletterSchema = new mongoose.Schema({
  email: {
    type: String,
    required: true,
    unique: true,
    lowercase: true
  }
}, {
  timestamps: true
});

// Investment Schema for tracking volume
const investmentSchema = new mongoose.Schema({
  userId: {
    type: mongoose.Schema.Types.ObjectId,
    ref: 'User',
    required: true
  },
  amount: {
    type: Number,
    required: true
  },
  packageType: {
    type: String,
    enum: ['fs_income', 'smartline', 'dri', 'das'],
    default: 'fs_income'
  },
  status: {
    type: String,
    enum: ['active', 'completed', 'cancelled'],
    default: 'active'
  },
  lockPeriod: {
    type: Number,
    default: 17 // months
  },
  unlockDate: {
    type: Date
  },
  totalReturns: {
    type: Number,
    default: 0
  },
  remainingReturns: {
    type: Number
  },
  dailyFsRate: {
    type: Number,
    default: 0.002727 // 6% monthly / 22 days
  },
  isActive: {
    type: Boolean,
    default: true
  }
}, {
  timestamps: true
});

// Transaction Schema for logging all activities
const transactionSchema = new mongoose.Schema({
  userId: {
    type: mongoose.Schema.Types.ObjectId,
    ref: 'User',
    required: true
  },
  type: {
    type: String,
    enum: ['deposit', 'fs_income', 'dri_income', 'smartline_income', 'das_income', 'withdrawal'],
    required: true
  },
  amount: {
    type: Number,
    required: true
  },
  description: {
    type: String,
    required: true
  },
  status: {
    type: String,
    enum: ['pending', 'completed', 'failed'],
    default: 'completed'
  },
  relatedInvestmentId: {
    type: mongoose.Schema.Types.ObjectId,
    ref: 'Investment',
    default: null
  },
  fromUserId: {
    type: mongoose.Schema.Types.ObjectId,
    ref: 'User',
    default: null
  },
  referralLevel: {
    type: Number,
    default: null
  }
}, {
  timestamps: true
});

// Deposit Schema
const depositSchema = new mongoose.Schema({
  userId: { type: mongoose.Schema.Types.ObjectId, ref: 'User', required: true },
  amount: { type: Number, required: true, min: 250 },
  walletType: { type: String, required: true },
  walletAddress: { type: String, required: true },
  paymentMethod: { type: String, default: 'USDT TRC-20' },
  screenshotPath: { type: String }, // Object storage path for payment screenshot
  screenshotUrl: { type: String }, // Upload URL for screenshot
  status: { 
    type: String, 
    enum: ['pending', 'confirmed', 'rejected'], 
    default: 'pending' 
  },
  adminActionAt: { type: Date },
  adminActionBy: { type: mongoose.Schema.Types.ObjectId, ref: 'User' },
  adminNotes: { type: String }
}, {
  timestamps: true
});

// Withdrawal Schema
const withdrawalSchema = new mongoose.Schema({
  userId: { type: mongoose.Schema.Types.ObjectId, ref: 'User', required: true },
  amount: { type: Number, required: true, min: 15 },
  requestedAmount: { type: Number, required: true }, // Original amount before service charge
  serviceCharge: { type: Number, required: true }, // 5% service charge
  method: { type: String, required: true },
  walletAddress: { type: String, required: true },
  status: { 
    type: String, 
    enum: ['pending_otp', 'pending_admin', 'approved', 'rejected', 'completed'], 
    default: 'pending_otp' 
  },
  otpVerified: { type: Boolean, default: false },
  otpVerifiedAt: { type: Date },
  createdAt: { type: Date, default: Date.now },
  updatedAt: { type: Date, default: Date.now },
  adminNotes: { type: String },
  adminActionAt: { type: Date },
  adminActionBy: { type: mongoose.Schema.Types.ObjectId, ref: 'User' }
});

// OTP Schema for withdrawal verification
const otpSchema = new mongoose.Schema({
  userId: { type: mongoose.Schema.Types.ObjectId, ref: 'User', required: true },
  withdrawalId: { type: mongoose.Schema.Types.ObjectId, ref: 'Withdrawal', required: true },
  otp: { type: String, required: true },
  purpose: { type: String, enum: ['withdrawal'], required: true },
  isUsed: { type: Boolean, default: false },
  expiresAt: { type: Date, required: true },
  createdAt: { type: Date, default: Date.now }
});

export const User = mongoose.model('User', userSchema);
export const Contact = mongoose.model('Contact', contactSchema);
export const Newsletter = mongoose.model('Newsletter', newsletterSchema);
export const Investment = mongoose.model('Investment', investmentSchema);
export const Deposit = mongoose.model('Deposit', depositSchema);
export const Transaction = mongoose.model('Transaction', transactionSchema);
export const Withdrawal = mongoose.model('Withdrawal', withdrawalSchema);
export const OTP = mongoose.model('OTP', otpSchema);

export default connectDB;

server/referralService.js.content:
import { User } from './database.js';

// Reward percentages for each level
const REWARD_RATES = {
  1: 0.015, // 1.5%
  2: 0.010, // 1.0%
  3: 0.0075, // 0.75%
  4: 0.005, // 0.50%
  5: 0.0025 // 0.25%
};

class ReferralService {

  // Update referral counts when a new user is registered
  async updateReferralCounts(newUserId) {
    try {
      const newUser = await User.findById(newUserId).populate('parent');
      if (!newUser || !newUser.parent) return;

      let currentParent = newUser.parent;
      let level = 1;

      // Traverse up the referral chain for 5 levels
      while (currentParent && level <= 5) {
        const updateField = `level${level}Count`;

        await User.findByIdAndUpdate(currentParent._id, {
          $inc: { 
            [updateField]: 1,
            referralCount: level === 1 ? 1 : 0 // Only count direct referrals in referralCount
          }
        });

        // Get the next parent
        const parentUser = await User.findById(currentParent._id).populate('parent');
        currentParent = parentUser?.parent;
        level++;
      }
    } catch (error) {
      console.error('Error updating referral counts:', error);
    }
  }

  // Calculate and distribute rewards when an investment is made
  async distributeRewards(investorId, investmentAmount) {
    try {
      const investor = await User.findById(investorId).populate('parent');
      if (!investor || !investor.parent) return;

      let currentParent = investor.parent;
      let level = 1;
      const rewards = [];

      // Traverse up the referral chain for 5 levels
      while (currentParent && level <= 5) {
        const rewardRate = REWARD_RATES[level];
        const rewardAmount = investmentAmount * rewardRate;

        const levelEarningsField = `level${level}Earnings`;

        // Update parent's earnings
        await User.findByIdAndUpdate(currentParent._id, {
          $inc: {
            [levelEarningsField]: rewardAmount,
            totalEarnings: rewardAmount
          }
        });

        rewards.push({
          userId: currentParent._id,
          level: level,
          amount: rewardAmount,
          rate: rewardRate
        });

        // Get the next parent
        const parentUser = await User.findById(currentParent._id).populate('parent');
        currentParent = parentUser?.parent;
        level++;
      }

      return rewards;
    } catch (error) {
      console.error('Error distributing rewards:', error);
      return [];
    }
  }

  // Get detailed referral statistics for a user
  async getReferralStats(userId) {
    try {
      const user = await User.findById(userId);
      if (!user) {
        return {
          level1Count: 0, level2Count: 0, level3Count: 0, level4Count: 0, level5Count: 0,
          totalReferrals: 0, totalEarnings: 0,
          level1Earnings: 0, level2Earnings: 0, level3Earnings: 0, level4Earnings: 0, level5Earnings: 0
        };
      }

      const stats = {
        level1Count: user.level1Count || 0,
        level2Count: user.level2Count || 0,
        level3Count: user.level3Count || 0,
        level4Count: user.level4Count || 0,
        level5Count: user.level5Count || 0,
        totalReferrals: (user.level1Count || 0) + (user.level2Count || 0) + 
                       (user.level3Count || 0) + (user.level4Count || 0) + (user.level5Count || 0),
        totalEarnings: user.totalEarnings || 0,
        level1Earnings: user.level1Earnings || 0,
        level2Earnings: user.level2Earnings || 0,
        level3Earnings: user.level3Earnings || 0,
        level4Earnings: user.level4Earnings || 0,
        level5Earnings: user.level5Earnings || 0
      };

      return stats;
    } catch (error) {
      console.error('Error getting referral stats:', error);
      return {
        level1Count: 0, level2Count: 0, level3Count: 0, level4Count: 0, level5Count: 0,
        totalReferrals: 0, totalEarnings: 0,
        level1Earnings: 0, level2Earnings: 0, level3Earnings: 0, level4Earnings: 0, level5Earnings: 0
      };
    }
  }

  // Get referral tree for a user (limited depth for performance)
  async getReferralTree(userId, maxDepth = 3) {
    try {
      const buildTree = async (parentId, currentDepth) => {
        if (currentDepth > maxDepth) return [];

        const children = await User.find({ parent: parentId })
          .select('firstName lastName email ownSponsorId createdAt level1Count level2Count level3Count level4Count level5Count')
          .lean();

        const tree = [];
        for (const child of children) {
          const childTree = await buildTree(child._id, currentDepth + 1);
          tree.push({
            ...child,
            children: childTree,
            level: currentDepth
          });
        }

        return tree;
      };

      return await buildTree(userId, 1);
    } catch (error) {
      console.error('Error getting referral tree:', error);
      return [];
    }
  }
}

export const referralService = new ReferralService();

