import React from 'react';
import { useLocation } from 'wouter';
import { 
  LayoutDashboard, 
  User, 
  CreditCard, 
  GitBranch, 
  ArrowDownToLine, 
  ArrowUpFromLine, 
  Receipt,
  LogOut,
  ChevronRight
} from 'lucide-react';

const ModernSidebar = ({ user, onLogout }) => {
  const [location, navigate] = useLocation();

  const menuItems = [
    {
      id: 'dashboard',
      label: 'Dashboard',
      icon: LayoutDashboard,
      path: '/dashboard',
      active: location === '/dashboard'
    },
    {
      id: 'profile',
