import { useState, useEffect } from 'react';
import { 
  Users, 
  DollarSign, 
  TrendingUp, 
  UserCheck,
  Eye,
  CheckCircle,
  XCircle,
  Clock,
  FileText,
  Search,
  Filter
} from 'lucide-react';
import axios from 'axios';
import Sidebar from '../components/Sidebar';

export default function AdminDashboard() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('users');
  const [searchTerm, setSearchTerm] = useState('');
  const [users, setUsers] = useState([]);
  const [deposits, setDeposits] = useState([]);
  const [withdrawals, setWithdrawals] = useState([]);
  const [kycSubmissions, setKycSubmissions] = useState([]);
  const [userHistory, setUserHistory] = useState(null);
  const [searchLoading, setSearchLoading] = useState(false);
  const [stats, setStats] = useState({
    totalUsers: 0,
    totalDeposits: 0,
    pendingDeposits: 0,
    totalVolume: 0,
    totalWithdrawals: 0,
    pendingWithdrawals: 0
  });

  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          window.location.href = '/login';
          return;
        }

        // Fetch user data
        const userResponse = await axios.get('/api/auth/me', {
          headers: { Authorization: `Bearer ${token}` }
        });

        if (!userResponse.data.isAdmin) {
          alert('Access denied. Admin privileges required.');
          window.location.href = '/dashboard';
          return;
        }

        setUser(userResponse.data);

        // Fetch admin data
        const [usersResponse, depositsResponse, withdrawalsResponse, kycResponse] = await Promise.all([
          axios.get('/api/admin/users', {
            headers: { Authorization: `Bearer ${token}` }
          }),
          axios.get('/api/admin/deposits', {
            headers: { Authorization: `Bearer ${token}` }
          }),
          axios.get('/api/admin/withdrawals', {
            headers: { Authorization: `Bearer ${token}` }
          }),
          axios.get('/api/admin/kyc', {
            headers: { Authorization: `Bearer ${token}` }
          })
        ]);

        setUsers(usersResponse.data);
        setDeposits(depositsResponse.data);
        setWithdrawals(withdrawalsResponse.data);
        setKycSubmissions(kycResponse.data);

        // Calculate stats
        const totalDeposits = depositsResponse.data.length;
        const pendingDeposits = depositsResponse.data.filter(d => d.status === 'pending').length;
        const totalVolume = depositsResponse.data
          .filter(d => d.status === 'confirmed')
          .reduce((sum, d) => sum + d.amount, 0);

        const totalWithdrawals = withdrawalsResponse.data.length;
        const pendingWithdrawals = withdrawalsResponse.data.filter(w => w.status === 'pending_admin').length;

        setStats({
          totalUsers: usersResponse.data.length,
          totalDeposits,
          pendingDeposits,
          totalVolume,
          totalWithdrawals,
          pendingWithdrawals
        });

      } catch (error) {
        if (error.response?.status === 401) {
          localStorage.removeItem('token');
          window.location.href = '/login';
        }
        console.error('Error fetching admin data:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    window.location.href = '/';
  };

  // KYC Management Functions
  const handleViewKycDocument = async (userId) => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`/api/kyc/document/${userId}`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      window.open(url, '_blank');
    } catch (error) {
      console.error('Error viewing KYC document:', error);
      alert('Failed to load KYC document. Please try again.');
    }
  };

  const handleKycApproval = async (userId, action) => {
    try {
      const token = localStorage.getItem('token');
      let reason = null;

      if (action === 'reject') {
        reason = prompt('Please provide a reason for rejection:');
        if (!reason) return; // User cancelled
      }

      const endpoint = action === 'approve' ? 
        `/api/admin/kyc/${userId}/approve` : 
        `/api/admin/kyc/${userId}/reject`;

      await axios.post(endpoint, { reason }, {
        headers: { Authorization: `Bearer ${token}` }
      });

      // Refresh KYC and users data
      const [usersResponse, kycResponse] = await Promise.all([
        axios.get('/api/admin/users', {
          headers: { Authorization: `Bearer ${token}` }
        }),
        axios.get('/api/admin/kyc', {
          headers: { Authorization: `Bearer ${token}` }
        })
      ]);
      setUsers(usersResponse.data);
      setKycSubmissions(kycResponse.data);

      alert(`KYC document ${action === 'approve' ? 'approved' : 'rejected'} successfully!`);
    } catch (error) {
      console.error(`Error ${action}ing KYC document:`, error);
      alert(`Failed to ${action} KYC document. Please try again.`);
    }
  };

  const handleDepositAction = async (depositId, action, notes = '') => {
    try {
      const token = localStorage.getItem('token');
      await axios.post(`/api/admin/deposits/${depositId}/action`, {
        action,
        notes: notes.trim()
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });

      // Refresh deposits
      const depositsResponse = await axios.get('/api/admin/deposits', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setDeposits(depositsResponse.data);

      // Update stats
      const totalDeposits = depositsResponse.data.length;
      const pendingDeposits = depositsResponse.data.filter(d => d.status === 'pending').length;
      const totalVolume = depositsResponse.data
        .filter(d => d.status === 'confirmed')
        .reduce((sum, d) => sum + d.amount, 0);

      setStats(prev => ({
        ...prev,
        totalDeposits,
        pendingDeposits,
        totalVolume
      }));

      alert(`Deposit ${action}d successfully!`);
    } catch (error) {
      console.error('Error updating deposit:', error);
      alert('Error updating deposit status');
    }
  };

  const handleViewDepositScreenshot = async (deposit) => {
    if (!deposit.screenshotPath) {
      alert('No screenshot available for this deposit');
      return;
    }

    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`/deposits/screenshot${deposit.screenshotPath}`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.ok) {
        const blob = await response.blob();
        const url = URL.createObjectURL(blob);
        window.open(url, '_blank', 'width=800,height=600');
      } else {
        alert('Failed to load screenshot. Please try again.');
      }
    } catch (error) {
      console.error('Error viewing screenshot:', error);
      alert('Error loading screenshot');
    }
  };

  const handleWithdrawalAction = async (withdrawalId, action, notes) => {
    if (!notes?.trim()) {
      alert('Please provide notes for this action');
      return;
    }

    try {
      const token = localStorage.getItem('token');
      await axios.post(`/api/admin/withdrawals/${withdrawalId}/action`, {
        action,
        notes: notes.trim()
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });

      // Refresh withdrawals
      const withdrawalsResponse = await axios.get('/api/admin/withdrawals', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setWithdrawals(withdrawalsResponse.data);

      // Update stats
      const pendingWithdrawals = withdrawalsResponse.data.filter(w => w.status === 'pending_admin').length;
      setStats(prev => ({
        ...prev,
        pendingWithdrawals
      }));

      alert(`Withdrawal ${action}d successfully!`);
    } catch (error) {
      console.error('Error processing withdrawal:', error);
      alert('Error processing withdrawal action');
    }
  };

  const handleUserSearch = async (searchTerm) => {
    if (!searchTerm.trim()) {
      alert('Please enter an email or mobile number');
      return;
    }

    setSearchLoading(true);
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get(`/api/admin/user-history/${encodeURIComponent(searchTerm)}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setUserHistory(response.data);
    } catch (error) {
      console.error('Error searching user:', error);
      alert('User not found or error occurred');
      setUserHistory(null);
    } finally {
      setSearchLoading(false);
    }
  };

  if (loading) {
    return (
      <div style={{ display: 'flex', height: '100vh', backgroundColor: '#f3f4f6' }}>
        <div style={{ width: '16rem', backgroundColor: '#1f2937' }}></div>
        <div style={{ flex: 1, display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
          <div style={{
            width: '3rem',
            height: '3rem',
            border: '2px solid #e5e7eb',
            borderTop: '2px solid #3b82f6',
            borderRadius: '50%',
            animation: 'spin 1s linear infinite'
          }}></div>
        </div>
      </div>
    );
  }

  return (
    <div style={{ display: 'flex', height: '100vh', backgroundColor: '#f3f4f6' }}>
      <Sidebar user={user} onLogout={handleLogout} />

      <div style={{ flex: 1, display: 'flex', flexDirection: 'column', overflow: 'hidden' }}>
        <header style={{
          backgroundColor: '#ffffff',
          boxShadow: '0 1px 3px 0 rgba(0, 0, 0, 0.1)',
          borderBottom: '1px solid #e5e7eb',
          padding: '1rem 1.5rem'
        }}>
          <h1 style={{
            fontSize: '1.5rem',
            fontWeight: 'bold',
            color: '#111827',
            margin: 0,
            display: 'flex',
            alignItems: 'center',
            gap: '0.5rem'
          }}>
            <UserCheck style={{ width: '1.5rem', height: '1.5rem', color: '#f59e0b' }} />
            Admin Dashboard
          </h1>
        </header>

        <main style={{
          flex: 1,
          overflowY: 'auto',
          backgroundColor: '#f9fafb',
          padding: '1.5rem'
        }}>
          {/* Stats Cards */}
          <div style={{
            display: 'grid',
            gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))',
            gap: '1.5rem',
            marginBottom: '2rem'
          }}>
            <div style={{
              backgroundColor: '#ffffff',
              borderRadius: '1rem',
              padding: '1.5rem',
              boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)',
              border: '1px solid #e5e7eb'
            }}>
              <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
                <div>
                  <p style={{ fontSize: '0.875rem', color: '#6b7280', margin: 0 }}>Total Users</p>
                  <p style={{ fontSize: '2rem', fontWeight: 'bold', color: '#111827', margin: 0 }}>
                    {stats.totalUsers}
                  </p>
                </div>
                <Users style={{ width: '2.5rem', height: '2.5rem', color: '#3b82f6' }} />
              </div>
            </div>

            <div style={{
              backgroundColor: '#ffffff',
              borderRadius: '1rem',
              padding: '1.5rem',
              boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)',
              border: '1px solid #e5e7eb'
            }}>
              <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
                <div>
                  <p style={{ fontSize: '0.875rem', color: '#6b7280', margin: 0 }}>Total Deposits</p>
                  <p style={{ fontSize: '2rem', fontWeight: 'bold', color: '#111827', margin: 0 }}>
                    {stats.totalDeposits}
                  </p>
                </div>
                <DollarSign style={{ width: '2.5rem', height: '2.5rem', color: '#10b981' }} />
              </div>
            </div>

            <div style={{
              backgroundColor: '#ffffff',
              borderRadius: '1rem',
              padding: '1.5rem',
              boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)',
              border: '1px solid #e5e7eb'
            }}>
              <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
                <div>
                  <p style={{ fontSize: '0.875rem', color: '#6b7280', margin: 0 }}>Pending Deposits</p>
                  <p style={{ fontSize: '2rem', fontWeight: 'bold', color: '#111827', margin: 0 }}>
                    {stats.pendingDeposits}
                  </p>
                </div>
                <Clock style={{ width: '2.5rem', height: '2.5rem', color: '#f59e0b' }} />
              </div>
            </div>

            <div style={{
              backgroundColor: '#ffffff',
              borderRadius: '1rem',
              padding: '1.5rem',
              boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)',
              border: '1px solid #e5e7eb'
            }}>
              <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
                <div>
                  <p style={{ fontSize: '0.875rem', color: '#6b7280', margin: 0 }}>Total Volume</p>
                  <p style={{ fontSize: '2rem', fontWeight: 'bold', color: '#111827', margin: 0 }}>
                    ${stats.totalVolume.toLocaleString()}
                  </p>
                </div>
                <TrendingUp style={{ width: '2.5rem', height: '2.5rem', color: '#8b5cf6' }} />
              </div>
            </div>
          </div>

          {/* Navigation Tabs */}
          <div style={{
            backgroundColor: '#ffffff',
            borderRadius: '1rem',
            boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)',
            border: '1px solid #e5e7eb',
            overflow: 'hidden'
          }}>
            <div style={{
              display: 'flex',
              borderBottom: '1px solid #e5e7eb',
              overflowX: 'auto'
            }}>
              {[
                { id: 'users', label: 'User List' },
                { id: 'kyc', label: 'KYC Status' },
                { id: 'deposits', label: 'Deposit Requests' },
                { id: 'withdrawals', label: 'Withdrawal Requests' },
                { id: 'investments', label: 'Investments' },
                { id: 'engagement', label: 'Engagement' }
              ].map((tab) => (
                <button
                  key={tab.id}
                  onClick={() => setActiveTab(tab.id)}
                  style={{
                    padding: '1rem 1.5rem',
                    backgroundColor: activeTab === tab.id ? '#f59e0b' : 'transparent',
                    color: activeTab === tab.id ? 'white' : '#6b7280',
                    border: 'none',
                    fontSize: '0.875rem',
                    fontWeight: '600',
                    cursor: 'pointer',
                    transition: 'all 0.2s ease',
                    whiteSpace: 'nowrap'
                  }}
                >
                  {tab.label}
                </button>
              ))}
            </div>

            <div style={{ padding: '1.5rem' }}>
              {activeTab === 'users' && (
                <div>
                  <h3 style={{
                    fontSize: '1.125rem',
                    fontWeight: 'bold',
                    color: '#111827',
                    marginBottom: '1rem'
                  }}>
                    User Management
                  </h3>

                  <div style={{ overflowX: 'auto' }}>
                    <table style={{
                      width: '100%',
                      borderCollapse: 'collapse',
                      fontSize: '0.875rem'
                    }}>
                      <thead>
                        <tr style={{ backgroundColor: '#f9fafb' }}>
                          <th style={{
                            padding: '0.75rem',
                            textAlign: 'left',
                            fontWeight: '600',
                            color: '#374151',
                            borderBottom: '1px solid #e5e7eb'
                          }}>Name</th>
                          <th style={{
                            padding: '0.75rem',
                            textAlign: 'left',
                            fontWeight: '600',
                            color: '#374151',
                            borderBottom: '1px solid #e5e7eb'
                          }}>Email</th>
                          <th style={{
                            padding: '0.75rem',
                            textAlign: 'left',
                            fontWeight: '600',
                            color: '#374151',
                            borderBottom: '1px solid #e5e7eb'
                          }}>Sponsor ID</th>
                          <th style={{
                            padding: '0.75rem',
                            textAlign: 'left',
                            fontWeight: '600',
                            color: '#374151',
                            borderBottom: '1px solid #e5e7eb'
                          }}>Referrals</th>
                          <th style={{
                            padding: '0.75rem',
                            textAlign: 'left',
                            fontWeight: '600',
                            color: '#374151',
                            borderBottom: '1px solid #e5e7eb'
                          }}>DAS Status</th>
                          <th style={{
                            padding: '0.75rem',
                            textAlign: 'left',
                            fontWeight: '600',
                            color: '#374151',
                            borderBottom: '1px solid #e5e7eb'
                          }}>Joined</th>
                        </tr>
                      </thead>
                      <tbody>
                        {users.length === 0 ? (
                          <tr>
                            <td colSpan="6" style={{
                              padding: '2rem',
                              textAlign: 'center',
                              color: '#6b7280'
                            }}>
                              No users found
                            </td>
                          </tr>
                        ) : (
                          users.map((userData, index) => (
                            <tr key={userData._id} style={{
                              backgroundColor: index % 2 === 0 ? '#ffffff' : '#f9fafb'
                            }}>
                              <td style={{
                                padding: '0.75rem',
                                borderBottom: '1px solid #e5e7eb'
                              }}>
                                {userData.firstName && userData.lastName 
                                  ? `${userData.firstName} ${userData.lastName}` 
                                  : userData.email || 'N/A'}
                              </td>
                              <td style={{
                                padding: '0.75rem',
                                borderBottom: '1px solid #e5e7eb'
                              }}>
                                {userData.email || 'N/A'}
                              </td>
                              <td style={{
                                padding: '0.75rem',
                                borderBottom: '1px solid #e5e7eb',
                                fontFamily: 'monospace',
                                color: '#3b82f6'
                              }}>
                                {userData.ownSponsorId || 'N/A'}
                              </td>
                              <td style={{
                                padding: '0.75rem',
                                borderBottom: '1px solid #e5e7eb'
                              }}>
                                {userData.referralCount || 0}
                              </td>
                              <td style={{
                                padding: '0.75rem',
                                borderBottom: '1px solid #e5e7eb'
                              }}>
                                <span style={{
                                  padding: '0.25rem 0.5rem',
                                  borderRadius: '0.25rem',
                                  fontSize: '0.75rem',
                                  fontWeight: '600',
                                  backgroundColor: userData.isEnrolledInDas ? '#dcfce7' : '#f3f4f6',
                                  color: userData.isEnrolledInDas ? '#166534' : '#6b7280'
                                }}>
                                  {userData.isEnrolledInDas ? 'Enrolled' : 'Not Enrolled'}
                                </span>
                              </td>
                              <td style={{
                                padding: '0.75rem',
                                borderBottom: '1px solid #e5e7eb',
                                color: '#6b7280'
                              }}>
                                {userData.createdAt ? new Date(userData.createdAt).toLocaleDateString() : 'N/A'}
                              </td>
                            </tr>
                          ))
                        )}
                      </tbody>
                    </table>
                  </div>
                </div>
              )}

              {activeTab === 'kyc' && (
                <div>
                  <h3 style={{
                    fontSize: '1.125rem',
                    fontWeight: 'bold',
                    color: '#111827',
                    marginBottom: '1rem'
                  }}>
                    KYC Document Management
                  </h3>

                  <div style={{ overflowX: 'auto' }}>
                    <table style={{
                      width: '100%',
                      borderCollapse: 'collapse',
                      fontSize: '0.875rem'
                    }}>
                      <thead>
                        <tr style={{ backgroundColor: '#f9fafb' }}>
                          <th style={{
                            padding: '0.75rem',
                            textAlign: 'left',
                            fontWeight: '600',
                            color: '#374151',
                            borderBottom: '1px solid #e5e7eb'
                          }}>User</th>
                          <th style={{
                            padding: '0.75rem',
                            textAlign: 'left',
                            fontWeight: '600',
                            color: '#374151',
                            borderBottom: '1px solid #e5e7eb'
                          }}>Status</th>
                          <th style={{
                            padding: '0.75rem',
                            textAlign: 'left',
                            fontWeight: '600',
                            color: '#374151',
                            borderBottom: '1px solid #e5e7eb'
                          }}>Document</th>
                          <th style={{
                            padding: '0.75rem',
                            textAlign: 'left',
                            fontWeight: '600',
                            color: '#374151',
                            borderBottom: '1px solid #e5e7eb'
                          }}>Submitted</th>
                          <th style={{
                            padding: '0.75rem',
                            textAlign: 'left',
                            fontWeight: '600',
                            color: '#374151',
                            borderBottom: '1px solid #e5e7eb'
                          }}>Actions</th>
                        </tr>
                      </thead>
                      <tbody>
                        {kycSubmissions.length === 0 ? (
                          <tr>
                            <td colSpan="5" style={{
                              padding: '2rem',
                              textAlign: 'center',
                              color: '#6b7280'
                            }}>
