import { createServer } from "http";
import { getStorage } from "./storage.js";

export async function registerRoutes(app) {
  const storage = getStorage();
  const server = createServer(app);

  // API Routes
  app.get("/api/health", (req, res) => {
    res.json({ status: "ok", message: "FXBOT API is running" });
  });

  // Investment packages endpoint
  app.get("/api/packages", async (req, res) => {
    try {
      const packages = [
        {
          id: 1,
          name: "FS Income (FixSix)",
          type: "fixed",
          return: "6% Monthly until 2x",
          minimum: 250,
          duration: "~17 months"
        },
        {
          id: 2,
          name: "SmartLine Income",
          type: "affiliate",
          levels: 5,
          commission: "1.5% to 0.25%"
        },
        {
          id: 3,
          name: "DRI Income",
          type: "direct",
          commission: "6%",
          frequency: "Per Investment"
        },
        {
          id: 4,
          name: "DAS Income",
          type: "salary",
          tiers: 3,
          rewards: "$300 to 2% CTO"
        }
      ];
      res.json(packages);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch packages" });
    }
  });

  // Contact form endpoint
  app.post("/api/contact", async (req, res) => {
    try {
      const { firstName, lastName, email, subject, message } = req.body;

      if (!firstName || !lastName || !email || !message) {
        return res.status(400).json({ error: "All fields are required" });
      }

      // In a real application, you would save this to a database
      console.log("Contact form submission:", { firstName, lastName, email, subject, message });

      res.json({ success: true, message: "Thank you for your message! We will get back to you soon." });
    } catch (error) {
      res.status(500).json({ error: "Failed to send message" });
    }
  });

  // Newsletter subscription endpoint
  app.post("/api/newsletter", async (req, res) => {
    try {
      const { email } = req.body;

      if (!email) {
        return res.status(400).json({ error: "Email is required" });
      }

      // In a real application, you would save this to a database
      console.log("Newsletter subscription:", { email });

      res.json({ success: true, message: "Successfully subscribed to newsletter!" });
    } catch (error) {
      res.status(500).json({ error: "Failed to subscribe" });
    }
  });

  return server;
}

server/storage.js.content:
// Simple in-memory storage for development
class MemStorage {
  constructor() {
    this.data = {
      contacts: [],
      subscribers: [],
      users: []
    };
  }

  // Contact form submissions
  async createContact(contactData) {
    const contact = {
      id: Date.now().toString(),
      ...contactData,
      createdAt: new Date().toISOString()
    };
    this.data.contacts.push(contact);
    return contact;
  }

  async getContacts() {
    return this.data.contacts;
  }

  // Newsletter subscriptions
  async createSubscriber(email) {
    const subscriber = {
      id: Date.now().toString(),
      email,
      createdAt: new Date().toISOString()
    };
    this.data.subscribers.push(subscriber);
    return subscriber;
  }

  async getSubscribers() {
    return this.data.subscribers;
  }

  // Users (for future authentication)
  async createUser(userData) {
    const user = {
      id: Date.now().toString(),
      ...userData,
      createdAt: new Date().toISOString()
    };
    this.data.users.push(user);
    return user;
  }

  async getUserByEmail(email) {
    return this.data.users.find(user => user.email === email);
  }

  async getUsers() {
    return this.data.users;
  }
}

let storage = null;

export function getStorage() {
  if (!storage) {
    storage = new MemStorage();
  }
  return storage;
}

server/vite.js.content:
import express from "express";
import fs from "fs";
import path from "path";
import { createServer as createViteServer, createLogger } from "vite";
import { nanoid } from "nanoid";
import viteConfig from "../vite.config.js";

const viteLogger = createLogger();

export function log(message, source = "express") {
  const formattedTime = new Date().toLocaleTimeString("en-US", {
    hour: "numeric",
    minute: "2-digit",
    second: "2-digit",
    hour12: true,
  });

  console.log(`${formattedTime} [${source}] ${message}`);
}

export async function setupVite(app, server) {
  const serverOptions = {
    middlewareMode: true,
    hmr: { server },
    allowedHosts: true,
  };

  const vite = await createViteServer({
    ...viteConfig,
    configFile: false,
    customLogger: {
      ...viteLogger,
      error: (msg, options) => {
        viteLogger.error(msg, options);
        process.exit(1);
      },
    },
    server: serverOptions,
    appType: "custom",
  });

  app.use(vite.middlewares);
  app.use("*", async (req, res, next) => {
    const url = req.originalUrl;

    try {
      const clientTemplate = path.resolve(
        path.dirname(new URL(import.meta.url).pathname),
        "..",
        "client",
        "index.html",
      );

      // always reload the index.html file from disk incase it changes
      let template = await fs.promises.readFile(clientTemplate, "utf-8");
      template = template.replace(
        `src="/src/main.jsx"`,
        `src="/src/main.jsx?v=${nanoid()}"`,
      );
      const page = await vite.transformIndexHtml(url, template);
      res.status(200).set({ "Content-Type": "text/html" }).end(page);
    } catch (e) {
      vite.ssrFixStacktrace(e);
      next(e);
    }
  });
}

export function serveStatic(app) {
  const distPath = path.resolve(path.dirname(new URL(import.meta.url).pathname), "public");

  if (!fs.existsSync(distPath)) {
    throw new Error(
      `Could not find the build directory: ${distPath}, make sure to build the client first`,
    );
  }

  app.use(express.static(distPath));

  // fall through to index.html if the file doesn't exist
  app.use("*", (_req, res) => {
    res.sendFile(path.resolve(distPath, "index.html"));
  });
}

shared/schema.js.content:
import { z } from "zod";

// Contact form schema
export const contactSchema = z.object({
  firstName: z.string().min(1, "First name is required"),
  lastName: z.string().min(1, "Last name is required"),
  email: z.string().email("Valid email is required"),
  subject: z.string().min(1, "Subject is required"),
  message: z.string().min(10, "Message must be at least 10 characters")
});

// Newsletter subscription schema
export const newsletterSchema = z.object({
  email: z.string().email("Valid email is required")
});

// User registration schema (for future use)
export const userRegistrationSchema = z.object({
  firstName: z.string().min(1, "First name is required"),
  lastName: z.string().min(1, "Last name is required"),
  email: z.string().email("Valid email is required"),
  password: z.string().min(8, "Password must be at least 8 characters"),
  phone: z.string().optional(),
  country: z.string().optional()
});

// Investment package schema
export const investmentPackageSchema = z.object({
  id: z.number(),
  name: z.string(),
  type: z.enum(['fixed', 'affiliate', 'direct', 'salary']),
  minimum: z.number().optional(),
  return: z.string().optional(),
  commission: z.string().optional(),
  duration: z.string().optional()
});

