  font-size: var(--font-size-base);
  line-height: 1;
}

.btn-primary {
  background-color: var(--primary-gold);
  color: var(--dark-bg);
}

.btn-primary:hover {
  background-color: var(--hover-gold);
  transform: translateY(-2px);
}

.btn-secondary {
