  display: flex;
  align-items: center;
  justify-content: center;
  padding: 20px;
  font-family: 'Inter', sans-serif;
}

.login-card {
  background: white;
  border-radius: 20px;
  padding: 50px 40px;
  max-width: 450px;
  width: 100%;
  box-shadow: 0 25px 50px rgba(0, 0, 0, 0.15);
  text-align: center;
}

.login-logo {
  margin-bottom: 40px;
}

.login-logo img {
  width: 80px;
  height: 80px;
  margin-bottom: 15px;
