}

.login-logo h1 {
  font-size: 32px;
  font-weight: 700;
  color: #2d3748;
  margin: 0;
  letter-spacing: -0.5px;
}

.login-form h2 {
  font-size: 24px;
  font-weight: 600;
  color: #2d3748;
  margin-bottom: 30px;
}

.form-group {
  margin-bottom: 25px;
  text-align: left;
}

.form-group label {
  display: block;
  margin-bottom: 8px;
  font-weight: 500;
  color: #4a5568;
  font-size: 14px;
}

.form-group input {
  width: 100%;
  padding: 12px 16px;
  border: 2px solid #e2e8f0;
  border-radius: 10px;
  font-size: 16px;
  transition: all 0.3s ease;
  background: #f8fafc;
  box-sizing: border-box;
  color: #2d3748;
}

.form-group input:focus {
  outline: none;
  border-color: #667eea;
  background: white;
  box-shadow: 0 0 0 3px rgba(102, 126, 234, 0.1);
  color: #2d3748;
}

.form-actions {
  text-align: right;
  margin-bottom: 30px;
}

.forgot-password-link {
  color: #667eea;
  text-decoration: none;
  font-size: 14px;
  font-weight: 500;
  transition: color 0.3s ease;
}

.forgot-password-link:hover {
  color: #5a67d8;
  text-decoration: underline;
}

.login-button, .submit-button {
  width: 100%;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  color: white;
  border: none;
  padding: 14px;
  border-radius: 10px;
  font-size: 16px;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  margin-bottom: 30px;
}

.login-button:hover:not(:disabled), .submit-button:hover:not(:disabled) {
  transform: translateY(-2px);
  box-shadow: 0 10px 20px rgba(102, 126, 234, 0.3);
}

.login-button:disabled, .submit-button:disabled {
  opacity: 0.7;
  cursor: not-allowed;
}

.error-message {
  background: #fed7d7;
  color: #c53030;
  padding: 12px 16px;
  border-radius: 8px;
  margin-bottom: 20px;
  font-size: 14px;
  border: 1px solid #feb2b2;
}

.success-message {
  background: #c6f6d5;
  color: #22543d;
  padding: 12px 16px;
  border-radius: 8px;
  margin-bottom: 20px;
  font-size: 14px;
  border: 1px solid #9ae6b4;
}

.back-to-home, .back-to-login {
  border-top: 1px solid #e2e8f0;
  padding-top: 20px;
}

.back-to-home a, .back-to-login a {
  color: #718096;
  text-decoration: none;
  font-size: 14px;
  font-weight: 500;
  transition: color 0.3s ease;
}

.back-to-home a:hover, .back-to-login a:hover {
  color: #4a5568;
}

/* Forgot Password Specific Styles */
.forgot-password-container {
  min-height: 100vh;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 20px;
  font-family: 'Inter', sans-serif;
}

.forgot-password-card {
  background: white;
  border-radius: 20px;
  padding: 50px 40px;
  max-width: 450px;
  width: 100%;
  box-shadow: 0 25px 50px rgba(0, 0, 0, 0.15);
  text-align: center;
}

.logo {
  margin-bottom: 40px;
}

.logo img {
  width: 80px;
  height: 80px;
  margin-bottom: 15px;
  border-radius: 50%;
  object-fit: cover;
}

.logo h1 {
  font-size: 32px;
  font-weight: 700;
  color: #2d3748;
  margin: 0;
  letter-spacing: -0.5px;
}

.forgot-password-form h2 {
  font-size: 24px;
  font-weight: 600;
  color: #2d3748;
  margin-bottom: 15px;
}

.description {
  color: #718096;
  margin-bottom: 30px;
  font-size: 14px;
  line-height: 1.5;
}

/* Registration form specific styles */
.form-row {
  display: flex;
  gap: 1rem;
}

.form-row .form-group {
  flex: 1;
}

/* Required field indicator */
.required {
  color: #ef4444;
}

/* Form labels */
.form-label {
  display: block;
  margin-bottom: 8px;
  font-weight: 500;
  color: #4a5568;
  font-size: 14px;
}

/* Form inputs */
.form-input {
  width: 100%;
  padding: 12px 16px;
  border: 2px solid #e2e8f0;
  border-radius: 10px;
  font-size: 16px;
  transition: all 0.3s ease;
  background: #f8fafc;
  box-sizing: border-box;
  color: #2d3748;
}

.form-input:focus {
  outline: none;
  border-color: #667eea;
  background: white;
  box-shadow: 0 0 0 3px rgba(102, 126, 234, 0.1);
  color: #2d3748;
}

.form-input.error {
  border-color: #ef4444;
  background: #fef2f2;
}

/* Error messages */
.error-message {
  display: block;
  color: #ef4444;
  font-size: 12px;
  margin-top: 4px;
  font-weight: 500;
}

/* Checkbox styling */
.checkbox-container {
  display: flex;
  align-items: flex-start;
  gap: 0.5rem;
  margin-top: 8px;
}

.form-checkbox {
  width: 18px;
  height: 18px;
  margin: 0;
  cursor: pointer;
  accent-color: #667eea;
  margin-top: 2px;
}

.checkbox-label {
  font-size: 14px;
  color: #374151;
  line-height: 1.4;
  cursor: pointer;
  margin: 0;
}

.checkbox-label .link {
  color: #667eea;
  text-decoration: none;
  font-weight: 500;
}

.checkbox-label .link:hover {
  text-decoration: underline;
}

/* Login/register page headers */
.login-header {
  text-align: center;
  margin-bottom: 40px;
}

.login-logo {
  width: 80px;
  height: 80px;
  margin: 0 auto 20px;
  border-radius: 50%;
  object-fit: cover;
}

.login-title {
  font-size: 32px;
  font-weight: 700;
  color: #2d3748;
  margin: 0 0 10px 0;
  letter-spacing: -0.5px;
}

.login-subtitle {
  color: #718096;
  margin: 0;
  font-size: 16px;
}

.login-footer {
  border-top: 1px solid #e2e8f0;
  padding-top: 20px;
  text-align: center;
}

.login-footer .link {
  color: #667eea;
  text-decoration: none;
  font-weight: 500;
}

.login-footer .link:hover {
  text-decoration: underline;
}

@media (max-width: 768px) {
  .login-container, .forgot-password-container {
    padding: 10px;
  }

  .login-card, .forgot-password-card {
    padding: 40px 30px;
    max-width: 500px;
  }

  .login-logo h1, .logo h1, .login-title {
    font-size: 28px;
  }

  .login-form h2, .forgot-password-form h2 {
    font-size: 22px;
  }

  .form-row {
    flex-direction: column;
    gap: 0;
  }

  .checkbox-container {
    align-items: flex-start;
  }

  .checkbox-label {
    font-size: 13px;
  }
}
